function media(p,f,f2){function w(){if(typeof(window.innerWidth)=='number'){return window.innerWidth;}else if(document.documentElement.clientWidth){return document.documentElement.clientWidth;}else if(document.body.clientWidth){return document.body.clientWidth;}}var n=parseInt(p.slice(1));var b=p.slice(0,1);if(b == '>'){if(w() > n){f();}else{f2();}window.onresize = function(){if(w()>n){f();}else{f2();}}}else if(b=='<'){if(w()<n){f();}else{f2();}window.onresize=function(){if(w()<n){f();}else{f2();}}}else{console.log('WRONG!!!');}}